let tagSet=new Set;document.getElementById("tags").addEventListener("keydown",function(event){if(event.key==="Enter"||event.key===","){event.preventDefault();addTags(this.value);this.value=""}});function addTags(input){let tags=input.split(",").map(tag=>tag.trim()).filter(tag=>tag.length>1);tags.forEach(tag=>{let splitTags=tag.split(/\s+/);splitTags.forEach(subTag=>{if(!subTag.startsWith("#")){subTag="#"+subTag}if(!tagSet.has(subTag)){tagSet.add(subTag);displayTags()}})})}function displayTags(){let tagContainer=document.getElementById("tagContainer");tagContainer.innerHTML="";tagSet.forEach(tag=>{let tagElement=document.createElement("span");tagElement.className="tag";tagElement.textContent=tag;let removeButton=document.createElement("button");removeButton.textContent="Ã—";removeButton.className="remove-tag";removeButton.onclick=()=>removeTag(tag);tagElement.appendChild(removeButton);tagContainer.appendChild(tagElement)});const tag=document.getElementById("hiddenTags").value=[...tagSet].join(",");console.log(tag)}function removeTag(tag){tagSet.delete(tag);displayTags()}